import React, { Component} from 'react';
import './App.css';

class Prop extends Component {
    constructor(props){
        super(props);
        this.name=React.createRef();
        this.addProduct=this.addProduct.bind(this);
    }
    onClick(){
        console.log('Day la app cua toi');
    }
    addProduct(){
        console.log(this.name.current.value);
        alert(this.name.current.value);
    }
  render(){

  return (
   <div>

       <nav className="navbar navbar-inverse">   
           <div className="container-fluid">
                <a className="navbar-brand" href="#">Props-Refs-State</a> 
           </div>
       </nav>

       <div className="container">         
           <div className="row">
               <div className="row">

                   <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">

                        <div className="panel panel-danger">
                              <div className="panel-heading">
                                    <h3 className="panel-title">Them san pham</h3>
                              </div>
                              <div className="panel-body">
                                    <div className="form-group">
                                        <label >Ten san pham</label>
                                        <input type="text" className="form-control" id="" placeholder="Input field" ref={this.name}/>
                                    </div>
                                    <button type="submit" className="btn btn-primary" onClick={this.addProduct}>Submit</button>
                              </div>
                        </div>


                   </div>


                   <div>

                       <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">

                           <button type="button" className="btn btn-warning" onClick={this.onClick}>button</button>

                       </div>

                   </div>
               </div>

           </div>

       </div>


   </div>
  );
  }

}

export default Prop;
