import React,{ Component} from 'react';
class ColorPicker extends Component{
    constructor(props){
        super(props);
        this.state={
            colors: ['red','blue','green','pink','red','red','red','red','red','red','red','blue']
        };
        this.setActiveCL=this.setActiveCL.bind(this);
    }

    showColor(color){

        return(
            {backgroundColor:color}
        );
    }
    setActiveCL(color){
        this.props.layVe(color);
    }
    render(){
        var eleColor=this.state.colors.map((color,index)=>{

            if(index %7!==0)
            {
                return ( 

                        <span key={index} 
                        style={this.showColor(color)} 
                        className={ this.props.color===color ? 'active':''}
                        onClick={()=>this.setActiveCL(color)}></span>  

            );}
            else
            {
                <br key={index}></br>
            }

        });
        return(    
            <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
                <div className="panel panel-primary">
                      <div className="panel-heading">
                            <h3 className="panel-title">ColorPicker</h3>
                      </div>
                      <div className="panel-body">
                            {eleColor}
                            <hr/>
                      </div>
                </div>
            </div>

        );
    }
}
export default ColorPicker;
